class Forager(PSAgent):
    
    def __init__(self, state_space, **kwargs):
        """
        This class extends the general `PSAgent` class and adapts it to the phase changing scenarioÂ·

        Parameters
        ----------
            
        **kwargs : multiple
            Parameters of the class that defines the learning agent.

        """
        
        self.state_space = state_space
        
        super().__init__(**kwargs) #Passing arguments to parent class
        
        #initialize the phase duration counter 
        self.phase = 0
        self.duration = 0
        self.duration_binned = 0
    
    def act(self, action):
        """
        Agent performs the given action.

        Parameters
        ----------
        action : int (0, 1)
            1 if it changes phase, 0 otherwise
        """
        
        # If the agent changes direction   
        if action == 1:
            self.phase = 1 - self.phase
            self.duration = 0
        else:
            self.duration += 1        
            
    def bin_state(self):
        """
        Bin phase duration.

        Parameters
        ----------
        duration : int [0, tau]
            Duration of a phase in step time 
            
        """
        
        binned_duration = np.argwhere((self.state_space[1] - self.duration) <= 0)[-1][0]
        
        return binned_duration
        
        
          
    
